import './App.scss';
import Card from './components/card/card';
import React,{useState, useEffect} from 'react';
import getCardData from './services/getData';



const App = () =>  {
  const [data, setData] = useState([]);
  const [click, setClick] = useState(false);

  function returnCards() {
    if(click){
      return data;
    }
    else{
      return data.map((item) => item.id < 16);
    }
  }

  useEffect(() => {
    getCardData()
        .then( (data)=> {
          setData(data)
        })
  }, []);
  
  
  return (
    <main className="main">
      <description className="description">Похожие объвления</description>
      <div className="App">
        <Card data={returnCards()}/>
      </div>
      <div className="showMore" onClick={setClick(true)}>
        <span className="text">Показать еще</span>
        <span className="expand"></span>
      </div>
    </main>
  );
}

export default App;
