import React,{useState, useEffect} from 'react';
import CardClasses from './card.module.scss';
import Compare from '../compare/compare';
import Like from '../like/Like';
import SliderButtons from '../sliderButtons/sliderButtons';
import CardInfo from '../cardInfo/cardInfo';
import getCardData from '../../services/getData';




const Card = () => {
  const [data, setData] = useState();
  const checked = <div className="checked">Просмотрено</div>;
  

  const loadData = async () => {
    return await getCardData()
            .then((data)=> setData(data));
  }

  useEffect(() => {
    loadData();
  }, []);

  console.log("data from app",data);
  let asdas = ["asda", "asdasd"]
  
  return (
    <>
      {
        asdas.map((items) => 
        {if(items.id <= 16 ) {
          return <div className={CardClasses.wrapper}>
                  <div className={CardClasses.picture}>
                    <Compare/>
                    <Like/>
                    <SliderButtons/>
                  </div>
                  <CardInfo dataItems={items} />
                </div> 
        }else{
          return <></>;
        }
        })
      }
    </> 
  );
}

export default Card;
