import React,{useState, useEffect} from 'react';
import CardClasses from './card.module.scss';
import Compare from '../compare/compare';
import Like from '../like/Like';
import SliderButtons from '../sliderButtons/sliderButtons';
import CardInfo from '../cardInfo/cardInfo';
import getCardData from '../../services/getData';




const Card = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    getCardData()
        .then( (data)=> {
          setData(data)
        })
    
  }, []);

  const [checked, setChecked] = useState(<></>)
  useEffect(() => {
    if(data.seen){
      return  setChecked(<div className="checked">Просмотрено</div>)
    }
    else{
      return <></>
    }
  });

  console.log(checked)

  return (
    <>
      {
        data.map((items) => 
        {if(items.id <= 16 ) {
          return  <div className={CardClasses.wrapper}>
                    <div className={CardClasses.picture}>
                      {checked}
                      <Compare/>
                      <Like/>
                      <SliderButtons/>
                    </div>
                    <CardInfo dataItems={items} />
                  </div> 
        }else{
          return <></>;
        }
        })
      }
    </> 
  );
}

export default Card;
