import React,{useState, useEffect} from 'react';
import CardClasses from './card.module.scss';
import Compare from '../compare/compare';
import Like from '../like/Like';
import SliderButtons from '../sliderButtons/sliderButtons';
import CardInfo from '../cardInfo/cardInfo';




const Card = (props) => {
  const [checked, setChecked] = useState(props.data.seen);
  if (checked) {
    return setChecked(<div className={CardClasses.checked}>Просмотрено</div>)
  }
  /* необходимо вывести в карточке просмотрено по свойству seen  */
  return (
    <div className={CardClasses.wrapper}>
      <div className={CardClasses.picture}>
        {checked}
        <Compare/>
        <Like/>
        <SliderButtons/>
      </div>
      <CardInfo dataItems={props.data} />
    </div> 
  );
  
}

export default Card;
