import React, {useState} from 'react';
import CardClasses from './card.module.scss';
import Compare from '../compare/compare';
import Like from '../like/Like';
import SliderButtons from '../sliderButtons/sliderButtons';
import CardInfo from '../cardInfo/cardInfo';




const Card = (props) => {

  const [picture, changePicture] = useState();

  function checkSeen(){
    if(props.data.seen){
      return <div className={CardClasses.checked}>Просмотрено</div>
    }
    else{
      return <></>
    }
  }

  return (
    <div className={CardClasses.wrapper}>
      <div className={CardClasses.picture} style={picture}>
        {checkSeen()}
        <Compare data={props.data.seen}/>
        <Like data={props.data.seen}/>
        <SliderButtons callBack={changePicture()}/>
      </div>
      <CardInfo dataItems={props.data} />
    </div> 
  );
  
}

export default Card;
