import React,{useState} from 'react';
import sliderCl from './sliderButton.module.scss';


const SliderButtons = () => {
  const [clicked, setClick] = useState(false)
  const [slider, setSlider] = useState();
  function checkClick(){
    if(clicked){
      setSlider(
        {
          backgroundImage: "url(https://source.unsplash.com/random)",
          backgroundSize: "cover",
          backgroundPosition: "50% 50%"
        }
      )
      return setClick(false);
    }
  }


  return(
    <div className={sliderCl.sliderButtons}>
      <span className={sliderCl.button1} style={slider} onClick={() => {checkClick(); setClick(true);}}></span>
      <span className={sliderCl.button2} style={slider}></span>
      <span className={sliderCl.button3} style={slider}></span>
      <span className={sliderCl.button4} style={slider}></span>
    </div>
  );
}

export default SliderButtons;
