import './App.scss';
import Card from './components/card/card';
import React,{useState, useEffect} from 'react';
import getCardData from './services/getData';

const App = () =>  {
  const [data, setData] = useState([]);
  const [click, setClick] = useState(false);

  

  useEffect(() => {
    getCardData()
        .then( (data)=> {
          setData(data)
        })

  }, []); 
  
/* косяк в том что ты не рендеришь компоненту card 16 раз ты передаешь туда 16 элементов массива*/
  return (
    <main className="main">
      <div className="description">Похожие объвления</div>
      <div className="App">
        { 
          data.map((items) => { 
            function returnCards() {
              if(click){
                return <Card data={items}/>;
              }
              else{
                console.log(items.id <= 16)
                return <Card data={() => items.id <= 16}/> ;
              }
            }
            return returnCards();
          })
        }
      </div>
      <div className="showMore" onClick={() => setClick(true)}>
        <span className="text">Показать еще</span>
        <span className="expand"></span>
      </div>
    </main>
  );
}

export default App;
